sp_widget.client_script:
function(cabrillo) {
  /* widget controller */
  var c = this;

  c.isNative = cabrillo.isNative(); 
  cabrillo.viewLayout.setBottomButtons([{
    title: 'Scan Code',
    enabled: true,
    backgroundColor: '#2d86d5',
    textColor: '#FFFFFF'
  }], function(e) {
    c.getBarcode();
  });

  c.getBarcode = function() {
    cabrillo.camera.getBarcode().then(function(value) {
      c.barcode = value;
      c.server.get({
        action: "get_asset",
        barcode: c.barcode
      }).then(displaySuccessScreen);
    }, function(err) {
      cabrillo.log('Whoops.');
      c.barcode = "COFFEE_MACHINE_1011";
      c.server.get({
        action: "get_asset",
        barcode: c.barcode
      }).then(displaySuccessScreen);
    });
  }

  function displaySuccessScreen(response) {
    removeButtons();
    c.done = true;
    c.success = true;
    c.assetID = response.data.assetID;
  }

  function removeButtons() {
    cabrillo.viewLayout.setBottomButtons([], function(){});
  }
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */ 

  if (input && input.action == "get_asset") {
    data.assetID = input.barcode;
    return;
  }
})();
